/** @format */

import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { getCountries } from "../../redux/actions";

export default function Home() {
   const dispatch = useDispatch();
   useEffect(() => {
      dispatch(getCountries());
   }, [dispatch]);

   const handleClick = (e) => {
      e.preventDefault();
      dispatch(getCountries());
   };
   return (
      <div>
         <h1>Welcome to Countries PI</h1>
         <nav>
            <select>
               <option>By Activity</option>
               <option>Without Activities</option>
               <option>With Activities</option>
            </select>
            <div>
               <select>
                  <option>Countries Alphabetically</option>
                  <option>From A-Z</option>
                  <option>From Z-A</option>
               </select>
               <select>
                  <option>By Population</option>
                  <option>Oldest to Youngest</option>
                  <option>Youngest to Oldest</option>
               </select>
               <select>
                  <option>All</option>
                  <option>Asia</option>
                  <option>Oceania</option>
                  <option>Europe</option>
                  <option>Americas</option>
                  <option>Antarctic</option>
                  <option>Africa</option>
               </select>
            </div>
            <div>
               <button
                  onClick={(e) => {
                     handleClick(e);
                  }}>
                  LOAD COUNTRIES
               </button>
            </div>
         </nav>
      </div>
   );
}
