/** @format */
import React, { useState } from "react";
import { useDispatch } from "react-redux";

const initialFormValues = {
   countryName: "",
   activityName: "",
   difficulty: "",
   duration: "",
   season: "",
};

const Form = () => {
   const [formValues, setFormValues] = useState(initialFormValues);

   const dispatch = useDispatch();

   const onSubmit = (e) => {
      e.preventDefault(e);
      dispatch(e);
   };
   const handleInputChange = (e) => {
      const changedFormValues = {
         ...formValues,
         [e.target.name]: e.target.value,
      };
      setFormValues(changedFormValues);
   };

   return (
      <div>
         <form>
            <table align='center' border='1' bgcolor='lightblue' width='400'>
               <caption bgcolor='darkblue'>
                  <marquee
                     direction='down'
                     width='400'
                     height='200'
                     behavior='alternate'>
                     <marquee behavior='alternate'>
                        <h1>Add New Activity</h1>{" "}
                     </marquee>
                  </marquee>
               </caption>
               <br />
               <tr bgcolor='lightblue'>
                  <td>
                     <label>Country Name: </label>
                  </td>
                  <td>
                     <input
                        value={countryName}
                        name='countryName'
                        onChange={handleInputChange}
                        size='25'
                        type='text'
                        placeholder='Type the name of the country'></input>
                  </td>
               </tr>
               <tr bgcolor='lightblue'>
                  <td>
                     <label>Activity Name: </label>
                  </td>
                  <td>
                     <input
                        value={activityName}
                        onChange={handleInputChange}
                        name='activityName'
                        size='25'
                        type='text'
                        placeholder='Type the name of the activity'></input>
                  </td>
               </tr>
               <tr bgcolor='lightblue'>
                  <td>
                     <label>Difficulty: </label>
                  </td>
                  <td>
                     <input
                        value={difficulty}
                        onChange={handleInputChange}
                        name='difficulty'
                        size='25'
                        type='text'
                        placeholder='Level of difficulty from 1-5'></input>
                  </td>
               </tr>
               <tr bgcolor='lightblue'>
                  <td>
                     <label>Duration: </label>
                  </td>
                  <td>
                     <input
                        value={duration}
                        onChange={handleInputChange}
                        name='duration'
                        size='25'
                        type='text'
                        placeholder='How long it takes'></input>
                  </td>
               </tr>
               <tr bgcolor='lightblue'>
                  <td>
                     <label>Season: </label>
                  </td>
                  <td>
                     <input
                        value={season}
                        onChange={handleInputChange}
                        name='season'
                        size='25'
                        type='text'
                        placeholder='Reasonable season to enjoy'></input>
                  </td>
               </tr>
            </table>
            <br />
            <hr />
            <br />
            <button name='Send' className='btn' type='submit'>
               CREATE
            </button>
         </form>
      </div>
   );
};

export default Form;
