/** @format */

import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries } from "../../redux/actions";
import Card from "../Card/Card";
import Page from "../Page/Page";

const allCountry = useSelector((state) => state.country);
const [currentPage, setCurrentPage] = useState(1);
const [countryPerPage, setCountryPerPage] = useState(10);
const indexOfLastCountry = currentPage * countryPerPage;
const indexOfFirsCountry = indexOfLastCountry - countryPerPage;

let currentCountry = allCountry.slice(indexOfFirsCountry, indexOfLastCountry);

const paginated = (pageNumber) => {
   setCurrentPage(pageNumber);
};
if (indexOfLastCountry === 10) {
   currentCountry = allCountry.slice(0, 9);
}

export default function Home() {
   const dispatch = useDispatch();
   useEffect(() => {
      dispatch(getCountries());
   }, [dispatch]);

   const handleClick = (e) => {
      e.preventDefault();
      dispatch(getCountries());
   };
   return (
      <div>
         <h1>Welcome to Countries PI</h1>
         <nav>
            <select>
               <option>By Activity</option>
               <option>Without Activities</option>
               <option>With Activities</option>
            </select>
            <div>
               <select>
                  <option>Countries Alphabetically</option>
                  <option>From A-Z</option>
                  <option>From Z-A</option>
               </select>
               <select>
                  <option>By Population</option>
                  <option>Oldest to Youngest</option>
                  <option>Youngest to Oldest</option>
               </select>
               <select>
                  <option>All</option>
                  <option>Asia</option>
                  <option>Oceania</option>
                  <option>Europe</option>
                  <option>Americas</option>
                  <option>Antarctic</option>
                  <option>Africa</option>
               </select>
            </div>
            <div>
               <button
                  onClick={(e) => {
                     handleClick(e);
                  }}>
                  LOAD COUNTRIES
               </button>
            </div>
         </nav>
         <div>
            {currentCountry.length ? (
               currentCountry.map((e) => {
                  return (
                     <div key={e.id}>
                        <Card
                           continent={e.continents}
                           img={e.flags}
                           name={e.name}
                           id={e.id}
                        />
                     </div>
                  );
               })
            ) : (
               <div>Loading...</div>
            )}
         </div>
         <div>{Page}</div>
      </div>
   );
}
