/** @format */
import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { newActivity, getCountries } from "../../redux/actions";
import Image from "./countries.png";

const Form = () => {
   const dispatch = useDispatch();
   const history = useHistory();
   const allCountry = useSelector((state) => state.country); /**idea orderer*/

   const [formValues, setFormValues] = useState({
      countries: "",
      name: "",
      difficulty: 0,
      duration: 0,
      season: "",
   });

   const [errors, setErrors] = useState({
      countries: "",
      name: "",
      difficulty: 0,
      duration: 0,
      season: "",
   });

   console.group();
   console.table(errors);
   console.groupEnd();

   function validateForm(form) {
      let errors = {};
      if (!form.countries) {
         errors.countries = "Country is required";
      }
      // if (form.name) {
      //    setErrors((errors.name = "Choose a country"));
      // }
      // if (form.difficulty !== "0") {
      //    alert("Difficulty must be from 1 to 5");
      //    errors.difficulty = "Choose a difficulty over 0";
      // }
      // if (form.duration !== 0) {
      //    alert("Duration must be from 1 to 24");
      //    setErrors((errors.duration = "Duration must be 1-24"));
      // }
      // if (form.season) {
      //    setErrors((errors.season = "Choose a season"));
      // }
      return setErrors(errors);
   }

   useEffect(() => {
      dispatch(getCountries());
   }, [dispatch]);

   const handleInputChange = (e) => {
      setFormValues({
         ...formValues,
         [e.target.name]: e.target.value,
      });
      setErrors(
         validateForm({
            ...formValues,
            [e.target.name]: e.target.value,
         })
      );
   };
   const onSubmit = (e) => {
      e.preventDefault(e); //evitamos el comportamiento que tiene por default el
      //evento onSubmit, lo que va a suceder es que no se va a recargar la pÃ¡gina

      if (!errors) {
         dispatch(newActivity(formValues));

         setFormValues({
            countries: "",
            name: "",
            difficulty: 0,
            duration: 0,
            season: "",
         });
         alert("Activity Created Succesfully");
         history.push("/home");
         console.group();
         console.table(formValues);
         console.groupEnd();
      } else {
         alert("Errors in page");
      }
   };

   return (
      <div
         className='home'
         style={{
            backgroundImage: `url(${Image})`,
            width: "100%",
            height: "1000px",
         }}>
         <div id='title-home'>COUNTRIES APP</div>
         <form onSubmit={onSubmit}>
            <div id='container-h2-form-page'>
               <p id='h2-form-page'> ADD NEW ACTIVITY</p>
            </div>

            <table align='center'>
               <tbody>
                  <tr>
                     <td>
                        <label>Country: </label>
                     </td>
                     <td>
                        <select
                           value={formValues.countries}
                           name='countries'
                           onChange={(e) => handleInputChange(e)}>
                           <option value={""}></option>
                           {allCountry.map((e) => (
                              <option type='text' value={e.name} key={e.id}>
                                 {e.name}
                              </option>
                           ))}
                        </select>
                     </td>
                  </tr>
                  <tr>
                     <td>
                        <label>Activity: </label>
                     </td>
                     <td>
                        <input
                           required
                           value={formValues.name}
                           onChange={handleInputChange}
                           name='name'
                           size='25'
                           type='text'
                           placeholder='Type the name of the activity'></input>
                     </td>
                  </tr>
                  <tr>
                     <td>
                        <label>Difficulty 1-5: </label>
                     </td>
                     <td>
                        <input
                           required
                           value={formValues.difficulty}
                           onChange={handleInputChange}
                           name='difficulty'
                           size='25'></input>
                        <label>{" " + formValues.difficulty}</label>
                     </td>
                  </tr>
                  <tr>
                     <td>
                        <label>Duration 1-24: </label>
                     </td>
                     <td>
                        <input
                           required
                           value={formValues.duration}
                           onChange={handleInputChange}
                           name='duration'
                           size='25'
                           type='range'
                           min='0'
                           max='24'></input>
                        <label>{" " + formValues.duration}</label>
                     </td>
                  </tr>
                  <tr>
                     <td>
                        <label>Season: </label>
                     </td>
                     <td>
                        <select
                           value={formValues.season}
                           name='season'
                           onChange={(e) => handleInputChange(e)}>
                           <option value={""}></option>
                           <option type='text' value={formValues.summer}>
                              Summer
                           </option>
                           <option type='text' value={formValues.spring}>
                              Spring
                           </option>
                           <option type='text' value={formValues.winter}>
                              Winter
                           </option>
                           <option type='text' value={formValues.autumn}>
                              Autumn
                           </option>
                        </select>
                     </td>
                  </tr>
               </tbody>
            </table>
            <br />
            <br />
            <button name='Send' className='button-form' type='submit'>
               CREATE
            </button>
         </form>
      </div>
   );
};

export default Form;
