/** @format */
import React, { useState } from "react";

const initialFormValues = {
   countryName: "country",
   activityName: "asdf",
   difficulty: "asdf",
   duration: "asdf",
   season: "asf",
};

const Form = () => {
   const [formValues, setFormValues] = useState(initialFormValues);
   const { countryName, activityName, difficulty, duration, season } =
      formValues;

   const handleInputChange = (e) => {
      const changedFormValues = {
         ...formValues,
         [e.target.name]: e.target.value,
      };
      setFormValues(changedFormValues);
   };

   return (
      <div>
         <form>
            <marquee
               direction='down'
               width='400'
               height='200'
               behavior='alternate'>
               <marquee behavior='alternate'>
                  <h1>Add New Activity</h1>{" "}
               </marquee>
            </marquee>

            <br />

            <label>Country Name: </label>

            <input
               value={countryName}
               name='countryName'
               handleInputChange={handleInputChange}
               size='25'
               type='text'
               placeholder='Type the name of the country'></input>

            <label>Activity Name: </label>

            <input
               value={activityName}
               handleInputChange={handleInputChange}
               name='activityName'
               size='25'
               type='text'
               placeholder='Type the name of the activity'></input>

            <label>Difficulty: </label>

            <input
               value={difficulty}
               handleInputChange={handleInputChange}
               name='difficulty'
               size='25'
               type='text'
               placeholder='Level of difficulty from 1-5'></input>

            <label>Duration: </label>

            <input
               value={duration}
               handleInputChange={handleInputChange}
               name='duration'
               size='25'
               type='text'
               placeholder='How long it takes'></input>

            <label>Season: </label>

            <input
               value={season}
               handleInputChange={handleInputChange}
               name='season'
               size='25'
               type='text'
               placeholder='Reasonable season to enjoy'></input>

            <br />
            <hr />
            <br />
            <button name='Send' className='btn btn-primary btn-block mt-2'>
               Send
            </button>
         </form>
      </div>
   );
};

export default Form;
