/** @format */

import React, { useState } from "react";
import { useSelector } from "react-redux";
import { getCountries } from "../../redux/actions";

function SearchBar() {
   const allCountry = useSelector((state) => state.country);
   const [name, setName] = useState("");
   console.log(name);

   function handleInputChange(e) {
      e.preventDefault();
      setName(e.target.value);
   }
   function handleSubmit(e) {
      e.preventDefault();
      const countryFilterByName = allCountry.filter((e) => {
         e.name.toLowerCase().includes(name.toLowerCase);
      });
      if (name && (!countryFilterByName || countryFilterByName.length === 0)) {
         alert("Country searched does not exists");
      } else if (!name) {
         alert("Type a country name");
      } else {
         dispatch(getCountries());
      }
   }

   return (
      <div>
         <input
            type='text'
            placeholder='Search Country'
            value={name}
            onChange={(e) => handleInputChange(e)}
         />

         <button onClick={console.log("probando boton")}>SEARCH</button>
      </div>
   );
}

export default SearchBar;
